import React, { useState } from "react";
import { useScrollPosition } from "../hooks/useScrollPosition";
import useResizeObserver from "../hooks/useResizeObserver";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import { mainBody, about, skills, experiences, education, project, hobbies, getInTouch } from "../editable-stuff/config.js";
import { NavLink } from "./home/migration";

const Navigation = React.forwardRef((props, ref) => {
  const [isTop, setIsTop] = useState(true);
  const [scrollPosition, setScrollPosition] = useState(0);
  const navbarMenuRef = React.useRef();
  const navbarDimensions = useResizeObserver(navbarMenuRef);
  const navBottom = navbarDimensions ? navbarDimensions.bottom : 0;
  useScrollPosition(
    ({ prevPos, currPos }) => {
      if (!navbarDimensions) return;
      currPos.y + ref.current.offsetTop - navbarDimensions.bottom > 5 ? setIsTop(true) : setIsTop(false);
      setScrollPosition(currPos.y);
    },
    [navBottom]
  );

  React.useEffect(() => {
    if (!navbarDimensions) return;
    navBottom - scrollPosition >= ref.current.offsetTop ? setIsTop(false) : setIsTop(true);
  }, [navBottom, navbarDimensions, ref, scrollPosition]);

  return (
    <Navbar ref={navbarMenuRef} className={`px-3 fixed-top  ${!isTop ? "navbar-white" : "navbar-transparent"}`} expand="lg">
      <Navbar.Brand className="navbar-brand" href={process.env.PUBLIC_URL + "/#home"}>
        Home
      </Navbar.Brand>
      <Navbar.Toggle aria-controls="basic-navbar-nav" className="toggler" />
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="navbar-nav mr-auto">
          {about.show && (
            <NavLink className="nav-item lead" href={process.env.PUBLIC_URL + "/#aboutme"}>
              About
            </NavLink>
          )}
          {skills.show && (
            <NavLink className="nav-item lead" href={process.env.PUBLIC_URL + "/#skills"}>
              Skills
            </NavLink>
          )}
          {education.show && (
            <NavLink className="nav-item lead" href={process.env.PUBLIC_URL + "/#education"}>
              Education
            </NavLink>
          )}
          {experiences.show && (
            <NavLink className="nav-item lead" href={process.env.PUBLIC_URL + "/#experiences"}>
              Experiences
            </NavLink>
          )}
          {project.show && (
            <NavLink className="nav-item lead" href={process.env.PUBLIC_URL + "/#project"}>
              Projects
            </NavLink>
          )}
          {hobbies.show && (
            <NavLink className="nav-item lead" href={process.env.PUBLIC_URL + "/#hobbies"}>
              Hobbies
            </NavLink>
          )}
          {getInTouch.show && (
            <NavLink className="nav-item lead" href={process.env.PUBLIC_URL + "/#getintouch"}>
              Contact
            </NavLink>
          )}
        </Nav>
      </Navbar.Collapse>
    </Navbar>
  );
});

export default Navigation;
